import React, { useEffect, useState } from 'react';
import {
    StyleSheet, Text, View, StatusBar, SafeAreaView, DataProvider, TouchableWithoutFeedback, Image, FlatList, Platform, ActivityIndicator, ScrollView, SectionList
} from 'react-native';
import { useDimensions} from '@react-native-community/hooks';
import { render } from 'react-dom';
import { WebView } from 'react-native-webview';
import { FontAwesomeIcon } from '@fortawesome/react-native-fontawesome'
import {
    faHistory, faHome, faSearch, faBookmark, faEllipsisV, faLanguage
} from './node_modules/@fortawesome/free-solid-svg-icons/index';
import { NavigationContainer } from '@react-navigation/native';
import { createStackNavigator } from '@react-navigation/stack';
import AsyncStorage from "@react-native-async-storage/async-storage";
import { text } from '@fortawesome/fontawesome-svg-core';


// Click Anime Functio

// Dub
function Dub({ navigation }) {

    const [isLoading, setLoading] = useState(true);
    var [data, setData] = useState([]);

    // Functions
    useEffect(() => {
        fetch("https://ajax.apimovie.xyz/ajax/page-recent-release.html?page=1&type=2")
            .then(function (resp) {
                return resp.text();
            })
            .then(function (data) {
                var Results = [{ "Name": [] }]
                var n = data.split(`<ul class="items">`);
                var m = n[1].split(`</ul>`);
                var o = m[0].split(`<li>`);
                for (var i = 1; i < o.length; i++) {
                    var p = o[i].split(`</li>`)
                    // Anime name
                    var name = p[0].split(`title="`);
                    var name1 = name[1].split(`">`)
                    // Image link
                    var image = p[0].split(`src="`)
                    var image1 = image[1].split(`"`)
                    // Episode Link so we can view
                    var link = p[0].split(`href="`)
                    var link1 = link[1].split(`"`)
                    // Episode Number
                    var episode = p[0].split(`<p class="episode">`)
                    var episode1 = episode[1].split(`</p>`);
                    // Adding Items to Json Array
                    Results[0].Name.push({ "id": i, "Name": name1[0], "Image": image1[0], "Link": link1[0], "Episode": episode1[0] });
                }
                setData(Results[0].Name);
                for (i = 0; i < Results.length; i++) {
                    var htmlstring = `
                        <li>
                            <div class="img">
                                <a href="${Results[i].Link}" title="${Results[i].Name}">
                                    <img src="${Results[i].Image}" alt="${Results[i].Name}">
                                    <div class="type ic-SUB"></div>
                                </a> 
                            </div> 
                            <p class="name">
                                <a href="${Results[i].Link}" title="">${Results[i].Name}</a>
                            </p> 
                            <p class="episode">${Results[i].Episode}</p> 
                        </li>                    
                    `
                }

            })
            .catch((error) => console.error(error))
            .finally(() => setLoading(false));
    }, []);
    const AnimeEpisode = (props) => {
        return (
            <View style={{ borderRadius: 10, borderColor: "white", borderWidth: 1, marginRight: 15, marginBottom: 15 }}>
                <Image style={{ width: 170, height: 250, borderRadius: 10 }} source={{ uri: props.img }} />
                <Text style={{ color: "white" }}>{props.name}</Text>
                <Text style={{ color: "white" }}>Episode {props.episode}</Text>
            </View>
        );
    }
    const ShowAnime = ({ item }) => (
        <View style={{ borderRadius: 10, overflow: "hidden", backgroundColor: "#2b2b2b", marginRight: 15, marginBottom: 15, maxWidth: 172 }}>
            <Image href={item.Link} style={{ width: 170, height: 250, }} source={{ "uri": item.Image }} />
            <Text style={{ marginTop: 5, color: "white", textAlign: 'center' }}>{item.Name}</Text>
            <Text style={{ color: "white", textAlign: 'center', marginBottom: 5 }}>{item.Episode}</Text>
        </View>
    )
    return (
        <SafeAreaView style={styles.container}>
            <View style={styles.top}>
                <Text style={styles.Text}>Anistream</Text>
                <View style={styles.iconContainer}>
                    <FontAwesomeIcon size={22} color={"white"} icon={faSearch}
                        style={styles.searchIcon}
                    />
                    <FontAwesomeIcon size={22} color={"white"} icon={faBookmark}
                        style={styles.searchIcon}
                    />
                    <FontAwesomeIcon size={22} color={"white"} icon={faEllipsisV}
                        style={styles.searchIcon}
                    />
                </View>
            </View>
            <View style={styles.AnimeContainer}>
                {isLoading ? <ActivityIndicator size="large" color="white" style={{
                    position: 'absolute',
                    left: 0,
                    right: 0,
                    top: 0,
                    bottom: 0,
                    alignItems: 'center',
                    justifyContent: 'center'
                }} /> : (
                        <FlatList numColumns="2" style={[{ flexDirection: 'row', flexWrap: 'wrap', marginLeft: 6 }]}
                            data={data}
                            keyExtractor={({ id }, index) => id.toString()}
                            renderItem={ShowAnime}
                            showsVerticalScrollIndicator={false}
                        />
                    )}
            </View>
            <View style={styles.bottom}>
                <View style={styles.bottomIconContainer}>
                    <FontAwesomeIcon size={45} color={"white"} onPress={() => navigation.push('Dub')} icon={faLanguage} style={{ flexGrow: 1 }}
                    />
                    <FontAwesomeIcon size={40} color={"#c4c4c4"} onPress={() => navigation.push('Home')} icon={faHome} style={{ marginTop: 2, flexGrow: 1 }}
                    />
                    <FontAwesomeIcon size={35} color={"#c4c4c4"} onPress={() => navigation.push('Recent')} icon={faHistory} style={{ marginTop: 5, flexGrow: 1 }}
                    />
                </View>
                <View style={styles.bottomTextContainer}>
                    <Text onPress={() => navigation.push('Dub')} style={[styles.bottomText, { marginLeft: 2, width: '33.33%', textAlign: 'center', }]}>Dub</Text>
                    <Text onPress={() => navigation.push('Home')} style={[styles.bottomText, { marginLeft: 0, marginLeft: 6, width: '33.33%', textAlign: 'center', color: '#c4c4c4' }]}>Sub</Text>
                    <Text onPress={() => navigation.push('Recent')} style={[styles.bottomText, { marginLeft: 0, width: '33.33%', textAlign: 'center', color: '#c4c4c4' }]}>Recent</Text>
                </View>
            </View>
        </SafeAreaView>
    );
}
// Sub
function Home({ navigation }) {

    const [isLoading, setLoading] = useState(true);
    var [data, setData] = useState([]);
    const [AnimeLink, SetLink] = useState(null);
        const save = async () => {
            try {
                await AsyncStorage.setItem("Anime", AnimeLink);
                navigation.push('Watch')
            } catch (error) {
                alert(error);
            }
        }
        
    useEffect(() => {
        if (AnimeLink !== null) {
            console.log("UseEffect" + AnimeLink);
            save();
        }
        SetLink(null);
    }, [AnimeLink]);
    // Functions
    useEffect(() => {
        fetch("https://gogoanime.vc")
            .then(function (resp) {
                return resp.text();
            })
            .then(function (data) {
                var Results = [{ "Name": [] }]
                var n = data.split(`<ul class="items">`);
                var m = n[1].split(`</ul>`);
                var o = m[0].split(`<li>`);
                for (var i = 1; i < o.length; i++) {
                    var p = o[i].split(`</li>`)
                    // Anime name
                    var name = p[0].split(`title="`);
                    var name1 = name[1].split(`">`)
                    // Image link
                    var image = p[0].split(`src="`)
                    var image1 = image[1].split(`"`)
                    // Episode Link so we can view
                    var link = p[0].split(`href="`)
                    var link1 = link[1].split(`"`)
                    // Episode Number
                    var episode = p[0].split(`<p class="episode">`)
                    var episode1 = episode[1].split(`</p>`);
                    // Adding Items to Json Array
                    Results[0].Name.push({ "id": i, "Name": name1[0], "Image": image1[0], "Link": link1[0], "Episode": episode1[0] });
                }
                setData(Results[0].Name);
                for (i = 0; i < Results.length; i++) {
                    var htmlstring = `
                        <li>
                            <div class="img">
                                <a href="${Results[i].Link}" title="${Results[i].Name}">
                                    <img src="${Results[i].Image}" alt="${Results[i].Name}">
                                    <div class="type ic-SUB"></div>
                                </a> 
                            </div> 
                            <p class="name">
                                <a href="${Results[i].Link}" title="">${Results[i].Name}</a>
                            </p> 
                            <p class="episode">${Results[i].Episode}</p> 
                        </li>                    
                    `
                }

            })
            .catch((error) => console.error(error))
            .finally(() => setLoading(false));
    }, []);
    const AnimeEpisode = (props) => {
        return (
            <View style={{ borderRadius: 10, borderColor: "white", borderWidth: 1, marginRight: 15, marginBottom: 15 }}>
                <Image style={{ width: 170, height: 250, borderRadius: 10 }} source={{ uri: props.img }} />
                <Text style={{ color: "white" }}>{props.name}</Text>
                <Text style={{ color: "white" }}>Episode {props.episode}</Text>
            </View>
        );
    }
    const ShowAnime = ({ item }) => (
        <View style={{ borderRadius: 10, overflow: "hidden", backgroundColor:"#2b2b2b" , marginRight: 15, marginBottom: 15, maxWidth:172}}>
            <TouchableWithoutFeedback onPress={() => {
                SetLink("https://gogoanime.vc" + item.Link)
            }}>
                <Image style={{ width: 170, height: 250, }} source={{ "uri": item.Image }} />
            </TouchableWithoutFeedback>
            <TouchableWithoutFeedback linkofAnime={item.Link} onPress={() => { SetLink("https://gogoanime.vc" + item.Link)}} >
                <Text style={{ marginTop: 5, color: "white", textAlign: 'center' }}>{item.Name}</Text>
            </TouchableWithoutFeedback>
            <TouchableWithoutFeedback linkofAnime={item.Link} onPress={() => { SetLink("https://gogoanime.vc" + item.Link)}}>
                <Text style={{ color: "white", textAlign: 'center', marginBottom: 5 }}>{item.Episode}</Text>
            </TouchableWithoutFeedback>
        </View>
    )
    return (
            <SafeAreaView style={styles.container}>
                <View style={styles.top}>
                    <Text style={styles.Text}>Anistream</Text>
                    <View style={styles.iconContainer}>
                        <FontAwesomeIcon size={22} color={"white"} icon={faSearch}
                            style={styles.searchIcon}
                        />
                        <FontAwesomeIcon size={22} color={"white"} icon={faBookmark}
                            style={styles.searchIcon}
                        />
                        <FontAwesomeIcon size={22} color={"white"} icon={faEllipsisV}
                            style={styles.searchIcon}
                        />
                    </View>
                </View>
                <View style={styles.AnimeContainer}>
                    {isLoading ? <ActivityIndicator size="large" color="white" style={{
                        position: 'absolute',
                        left: 0,
                        right: 0,
                        top: 0,
                        bottom: 0,
                        alignItems: 'center',
                        justifyContent: 'center'
                    }} /> : (
                            <FlatList numColumns="2" style={[{ flexDirection: 'row', flexWrap: 'wrap', marginLeft: 6 }]}
                                data={data}
                                keyExtractor={({ id }, index) => id.toString()}
                                renderItem={ShowAnime}
                                showsVerticalScrollIndicator={false}
                            />
                        )}
                </View>
                <View style={styles.bottom}>
                    <View style={styles.bottomIconContainer}>
                    <FontAwesomeIcon size={45} color={"#c4c4c4"} onPress={() => navigation.push('Dub')} icon={faLanguage} style={{ flexGrow: 1 }}
                            />
                    <FontAwesomeIcon size={40} color={"white"} onPress={() => navigation.push('Home')} icon={faHome} style={{ marginTop: 2, flexGrow: 1   }}
                        />
                    <FontAwesomeIcon size={35} color={"#c4c4c4"} onPress={() => navigation.push('Recent')} icon={faHistory} style={{ marginTop: 5, flexGrow: 1   }}
                        />
                    </View>
                    <View style={styles.bottomTextContainer}>
                    <Text onPress={() => navigation.push('Dub')} style={[styles.bottomText, { marginLeft: 2, width: '33.33%', textAlign: 'center', color:'#c4c4c4' }]}>Dub</Text>
                    <Text onPress={() => navigation.push('Home')} style={[styles.bottomText, { marginLeft: 0, marginLeft:6 , width: '33.33%', textAlign: 'center' }]}>Sub</Text>
                    <Text onPress={() => navigation.push('Recent')} style={[styles.bottomText, { marginLeft: 0, width: '33.33%', textAlign: 'center', color:'#c4c4c4' }]}>Recent</Text>
                    </View>
                </View>
            </SafeAreaView>
    );
}
//Recent
function Recent({ navigation }) {
    return (
        <SafeAreaView>
            <Text>Fuckciiccicici</Text>
        </SafeAreaView>
        );
}
// Watch Anime
function Watch({ navigation }) {
    const [AnimeLink, SetLink] = useState();
    const [VideoLink, SetVideo] = useState(null);
    const load = async () => {
        try {
            const value = await AsyncStorage.getItem('Anime');
                // We have data!!
            SetLink(value);
        } catch (error) {
            // Error retrieving data
            alert("nooo")
        }
    };
