        width: '100%',
        height: '8%',
        justifyContent: 'center',
        bottom: 0,
    },
    flaTContain: {
        justifyContent: 'center'
    },
    searchIcon: {
        marginBottom: 15,
        marginLeft: 30
    },
    bottomIcons: {

    },
    iconContainer: {
        flexDirection: "row",
    },
    bottomIconContainer: {
