import React, { useEffect, useState, useRef } from 'react';
import {
    StyleSheet, Text, View, StatusBar, SafeAreaView , DataProvider, TouchableWithoutFeedback, Image, FlatList, Platform, ActivityIndicator, ScrollView, SectionList
} from 'react-native';
import { useDimensions} from '@react-native-community/hooks';
import { render } from 'react-dom';
import { WebView } from 'react-native-webview';
import { FontAwesomeIcon } from '@fortawesome/react-native-fontawesome'
import {
    faHistory, faHome, faSearch, faBookmark, faEllipsisV, faLanguage, faFastForward
} from './node_modules/@fortawesome/free-solid-svg-icons/index';
import { NavigationContainer } from '@react-navigation/native';
import { createStackNavigator } from '@react-navigation/stack';
import AsyncStorage from "@react-native-async-storage/async-storage";
import { text } from '@fortawesome/fontawesome-svg-core';
import { Video } from 'expo-av';
import Dub from './Views/Dub'
import { set } from 'react-native-reanimated';


// Click Anime Functio
// Sub
function Home({ navigation }) {
    const RecentItems = [{ "Name": [] }]
    function addToRecent() {
        console.log("add To recent");
        console.log(RecentItems)
        var saveRI = async () => {
            try {
                await AsyncStorage.setItem('Recent', JSON.stringify(RecentItems));
            } catch (error) {
                alert(error);
            }
        }
        saveRI();
    }
    //
    const [isLoading, setLoading] = useState(true);
    var [data, setData] = useState([]);
    const [AnimeLink, SetLink] = useState(null);
        const save = async () => {
            try {
                await AsyncStorage.setItem("Anime", AnimeLink);
                navigation.push('Watch')
            } catch (error) {
                alert(error);
            }
    }

        
    useEffect(() => {
        if (AnimeLink !== null) {
            console.log("UseEffect" + AnimeLink);
            save();
        }
        SetLink(null);
    }, [AnimeLink]);
    // Functions
    useEffect(() => {
        fetch("https://gogoanime.vc")
            .then(function (resp) {
                return resp.text();
            })
            .then(function (data) {
                var Results = [{ "Name": [] }]
                var n = data.split(`<ul class="items">`);
                var m = n[1].split(`</ul>`);
                var o = m[0].split(`<li>`);
                for (var i = 1; i < o.length; i++) {
                    var p = o[i].split(`</li>`)
                    // Anime name
                    var name = p[0].split(`title="`);
                    var name1 = name[1].split(`">`)
                    // Image link
                    var image = p[0].split(`src="`)
                    var image1 = image[1].split(`"`)
                    // Episode Link so we can view
                    var link = p[0].split(`href="`)
                    var link1 = link[1].split(`"`)
                    // Episode Number
                    var episode = p[0].split(`<p class="episode">`)
                    var episode1 = episode[1].split(`</p>`);
                    // Adding Items to Json Array
                    Results[0].Name.push({ "id": i, "Name": name1[0], "Image": image1[0], "Link": link1[0], "Episode": episode1[0] });
                }
                setData(Results[0].Name);
                for (i = 0; i < Results.length; i++) {
                    var htmlstring = `
                        <li>
                            <div class="img">
                                <a href="${Results[i].Link}" title="${Results[i].Name}">
                                    <img src="${Results[i].Image}" alt="${Results[i].Name}">
                                    <div class="type ic-SUB"></div>
                                </a> 
                            </div> 
                            <p class="name">
                                <a href="${Results[i].Link}" title="">${Results[i].Name}</a>
                            </p> 
                            <p class="episode">${Results[i].Episode}</p> 
                        </li>                    
                    `
                }

            })
            .catch((error) => console.error(error))
            .finally(() => setLoading(false));
    }, []);
    const AnimeEpisode = (props) => {
        return (
            <View style={{ borderRadius: 10, borderColor: "white", borderWidth: 1, marginRight: 15, marginBottom: 15 }}>
                <Image style={{ width: 170, height: 250, borderRadius: 10 }} source={{ uri: props.img }} />
                <Text style={{ color: "white" }}>{props.name}</Text>
                <Text style={{ color: "white" }}>Episode {props.episode}</Text>
            </View>
        );
    }
    const ShowAnime = ({ item }) => (
        <View style={{ borderRadius: 10, overflow: "hidden", backgroundColor:"#2b2b2b" , marginRight: 15, marginBottom: 15, maxWidth:172}}>
            <TouchableWithoutFeedback onPress={() => {
                SetLink("https://gogoanime.vc" + item.Link), console.log("Anime is Clicked"),
                    RecentItems[0].Name.push({ "Name": item.Name, "Image": item.Image, "Link": item.Link, "Episode": item.Episode }),
                    addToRecent();
            }}>
                <Image style={{ width: 170, height: 250, }} source={{ "uri": item.Image }} />
            </TouchableWithoutFeedback>
            <TouchableWithoutFeedback linkofAnime={item.Link} onPress={() => {
                SetLink("https://gogoanime.vc" + item.Link), console.log("Anime is Clicked")
                    //saveRecent[0].Name.push({ "Name": item.Name, "Image": item.Image, "Link": item.Link, "Episode": item.Episode }),
                    //console.log(Recent)
            }} >
                <Text style={{ marginTop: 5, color: "white", textAlign: 'center' }}>{item.Name}</Text>
            </TouchableWithoutFeedback>
            <TouchableWithoutFeedback linkofAnime={item.Link} onPress={() => {
                SetLink("https://gogoanime.vc" + item.Link), console.log("Anime is Clicked")
                    //saveRecent[0].Name.push({ "Name": item.Name, "Image": item.Image, "Link": item.Link, "Episode": item.Episode }),
                    //console.log(Recent)
            }}>
                <Text style={{ color: "white", textAlign: 'center', marginBottom: 5 }}>{item.Episode}</Text>
            </TouchableWithoutFeedback>
        </View>
    )
    return (
            <SafeAreaView style={styles.container}>
                <View style={styles.top}>
                    <Text style={styles.Text}>Anistream</Text>
                    <View style={styles.iconContainer}>
                        <FontAwesomeIcon size={22} color={"white"} icon={faSearch}
                            style={styles.searchIcon}
                        />
                        <FontAwesomeIcon size={22} color={"white"} icon={faBookmark}
                            style={styles.searchIcon}
                        />
                        <FontAwesomeIcon size={22} color={"white"} icon={faEllipsisV}
                            style={styles.searchIcon}
                        />
                    </View>
                </View>
                <View style={styles.AnimeContainer}>
                    {isLoading ? <ActivityIndicator size="large" color="white" style={{
                        position: 'absolute',
                        left: 0,
                        right: 0,
                        top: 0,
                        bottom: 0,
                        alignItems: 'center',
                        justifyContent: 'center'
                    }} /> : (
                            <FlatList numColumns="2" style={[{ flexDirection: 'row', flexWrap: 'wrap', marginLeft: 6 }]}
                                data={data}
                                keyExtractor={({ id }, index) => id.toString()}
                                renderItem={ShowAnime}
                                showsVerticalScrollIndicator={false}
                            />
                        )}
                </View>
                <View style={styles.bottom}>
                    <View style={styles.bottomIconContainer}>
                    <FontAwesomeIcon size={45} color={"#c4c4c4"} onPress={() => navigation.push('Dub')} icon={faLanguage} style={{ flexGrow: 1 }}
                            />
                    <FontAwesomeIcon size={40} color={"white"} onPress={() => navigation.push('Home')} icon={faHome} style={{ marginTop: 2, flexGrow: 1   }}
                        />
                    <FontAwesomeIcon size={35} color={"#c4c4c4"} onPress={() => navigation.push('Recent')} icon={faHistory} style={{ marginTop: 5, flexGrow: 1   }}
                        />
                    </View>
                    <View style={styles.bottomTextContainer}>
                    <Text onPress={() => navigation.push('Dub')} style={[styles.bottomText, { marginLeft: 2, width: '33.33%', textAlign: 'center', color:'#c4c4c4' }]}>Dub</Text>
                    <Text onPress={() => navigation.push('Home')} style={[styles.bottomText, { marginLeft: 0, marginLeft:6 , width: '33.33%', textAlign: 'center' }]}>Sub</Text>
                    <Text onPress={() => navigation.push('Recent')} style={[styles.bottomText, { marginLeft: 0, width: '33.33%', textAlign: 'center', color:'#c4c4c4' }]}>Recent</Text>
                    </View>
                </View>
            </SafeAreaView>
    );
}
//Recent
function Recent({ navigation }) {
    var [AnimeList, SetAnimeList] = useState(null);
    var [ParsedAnimeList, SetParsed] = useState(null);
    const load = async () => {
        try {
            const value = await AsyncStorage.getItem('Recent');
            SetAnimeList(value);
            // We have data!!
        } catch (error) {
            // Error retrieving data
            alert("nooo")
        }
    };
    load();
    useEffect(() => {
        if (AnimeList !== null) {
            var animeparsed = JSON.parse(AnimeList);
            SetParsed(animeparsed.Name);
        }
    }, [AnimeList])
    useEffect(() => {
        if (ParsedAnimeList !== null) {
            console.log(ParsedAnimeList);
        }
    }, [ParsedAnimeList])
    return (
        <SafeAreaView>
            <Text></Text>
        </SafeAreaView>
        );
}
// Watch Anime
function Watch({ navigation }) {
    const [AnimeLink, SetLink] = useState(null);
    const [VideoLink, SetVideo] = useState(null);
    const [StorageLink, SetStorage] = useState(null);
    const videoRef = useRef(null)
    const [isLoading, setLoading] = useState(true);


    const load = async () => {
        try {
            const value = await AsyncStorage.getItem('Anime');
                // We have data!!
            SetLink(value);
        } catch (error) {
            // Error retrieving data
            alert("nooo")
        }
    };
    var request = new XMLHttpRequest();
    request.onreadystatechange = (e) => {
        if (request.readyState !== 4) {
            return;
        }
        if (request.status === 200) {
            var n = request.responseText.split("file:");
            var m = n[1].split(',label')
            var o = m[0].split(`'`)
            var p = o[1].split('https:')
            SetStorage("https:" + p[1]);
        } else {
            alert("no=bitch")
        }
    };
    // Create video
    function createVideo() {
        console.log("we have a video")
    }
    // Get Video
    function getVideoLink() {
        fetch(AnimeLink)
            .then(function (resp) {
                return resp.text();
            })
            .then(function (data) {
                var n = data.split("iframe");
                var m = n[1].split(`"`)
                var iframe = "https:" + m[1].replace("streaming", "loadserver");
                SetVideo(iframe)

            })
            .catch((error) => console.error(error))
    }
    useEffect(() => {
        if (AnimeLink !== null) {
            getVideoLink();
        }
    }, [AnimeLink]);
    useEffect(() => {
        if (VideoLink !== null) {
            console.log(VideoLink);
            request.open('GET', VideoLink);
            request.send();
        }
    }, [VideoLink]);
    useEffect(() => {
        if (StorageLink !== null) {
            console.log(StorageLink);
            setLoading(false);
        }
    }, [StorageLink]);
    load();
    return (
        <SafeAreaView style={{
            paddingTop: Platform.OS === "android" ? StatusBar.currentHeight : 0,
            flex: 1,
            backgroundColor:"black"
        }}>
            {isLoading ? <ActivityIndicator size="large" color="white" style={{
                position: 'absolute',
                left: 0,
                right: 0,
                top: 0,
                bottom: 0,
                alignItems: 'center',
                justifyContent: 'center'
            }} /> : (
                    <View>
                    <Video useNativeControls
                        rate={1.0}
                        volume={1.0}
                        isMuted={false}
                        resizeMode="contain"
                        shouldPlay
                        isLooping
                        ref={videoRef}
                        source={{ "uri": StorageLink }}
                        style={{
                            'width': '100%', 'height': '100%'
                            }} />
                    </View>
                )}
        </SafeAreaView>
    );
}
const Stack = createStackNavigator();

export default function App() {
    return (
        <NavigationContainer>
            <Stack.Navigator initialRouteName = "Home">
                <Stack.Screen options={{ headerShown: false }} name="Home" component={Home} />
                <Stack.Screen options={{ headerShown: false }} name="Dub" component={Dub} />
                <Stack.Screen  name="Recent" component={Recent} />
                <Stack.Screen options={{ headerShown: false }} name="Watch" component={Watch} />
            </Stack.Navigator>
        </NavigationContainer>
    );
}
const styles = StyleSheet.create({
    container: {
        flex: 1,
        backgroundColor: '#333131',
    },
    top: {
        backgroundColor: '#ed1915',
        paddingTop: Platform.OS === "android" ? StatusBar.currentHeight : 0,
        width: '100%',
        height: '10%',
        paddingLeft: 10,
        paddingRight: 10,
        flexDirection: "row",
        justifyContent: "space-between",
        alignItems: "center"
    },
    Text: {
        color: 'white',
        fontSize: 24,
        alignSelf: "flex-start",
    },
    bottom: {
        backgroundColor: '#ed1915',
        width: '100%',
        height: '8%',
        justifyContent: 'center',
        bottom: 0,
    },
    flaTContain: {
        justifyContent: 'center'
    },
    searchIcon: {
        marginBottom: 15,
        marginLeft: 30
    },
    bottomIcons: {

    },
    iconContainer: {
        flexDirection: "row",
    },
    bottomIconContainer: {
        flex: 0.5,
        flexDirection: 'row',
        justifyContent: 'space-around'
    },
    bottomTextContainer: {
        flex: 0.5,
        flexDirection: 'row',
        justifyContent: 'space-around'
    },
    bottomText: {
        color: 'white',
        marginTop: 5,
        fontSize: 17,
    },
    AnimeContainer: {
        flex: 1,
        padding: 8,
    }
});
