import React, { useEffect, useState, useRef } from 'react';
import {
    StyleSheet, Text, View, StatusBar, SafeAreaView, Button, DataProvider, Picker, DrawerLayoutAndroid, TouchableWithoutFeedback, Image, FlatList, Platform, ActivityIndicator, ScrollView, SectionList
} from 'react-native';
import { FontAwesomeIcon } from '@fortawesome/react-native-fontawesome'
import {
    faHistory, faHome, faSearch, faBookmark, faEllipsisV, faLanguage, faFastForward
} from '@fortawesome/free-solid-svg-icons/index';
import AsyncStorage from "@react-native-async-storage/async-storage";
import MoreOptions from '../Views/MoreOptions'

export default function Navbar () {
    const [DontshowOptions, SetMoreOptions] = useState(true);
    const [drawerPosition, setDrawerPosition] = useState("left");
    // More
    const navigationView = () => {
        <View style={[styles.container, styles.navigationContainer]}>
            <Text style={styles.paragraph}>I'm in the Drawer!</Text>
            <Button
                title="Close drawer"
                onPress={() => drawer.current.closeDrawer()}
            />
        </View>
    }
    return (
        <View style={styles.top}>
            <Text style={styles.Text}>Anistream</Text>
            <View style={styles.iconContainer}>
                <FontAwesomeIcon size={22} color={"white"} icon={faSearch}
                    style={styles.searchIcon}
                />
                <FontAwesomeIcon size={22} color={"white"} icon={faBookmark}
                    style={styles.searchIcon}
                />
                    <TouchableWithoutFeedback onPress={() => drawer.current.openDrawer()}>
                    <FontAwesomeIcon size={22} color={"white"} icon={faEllipsisV}
                    style={styles.searchIcon}
                    />
                </TouchableWithoutFeedback>
            </View>
            </View>
        
        )
}

const styles = StyleSheet.create({
    container: {
        flex: 1,
        backgroundColor: '#333131',
    },
    top: {
        backgroundColor: '#ed1915',
        paddingTop: Platform.OS === "android" ? StatusBar.currentHeight : 0,
        width: '100%',
        height: '10%',
        paddingLeft: 10,
        paddingRight: 10,
        flexDirection: "row",
        justifyContent: "space-between",
        alignItems: "center"
    },
    Text: {
        color: 'white',
        fontSize: 24,
        alignSelf: "flex-start",
    },
    bottom: {
        backgroundColor: '#ed1915',
        width: '100%',
        height: '8%',
        justifyContent: 'center',
        bottom: 0,
    },
    flaTContain: {
        justifyContent: 'center'
    },
    searchIcon: {
        marginBottom: 15,
        marginLeft: 30
    },
    bottomIcons: {

    },
    iconContainer: {
        flexDirection: "row",
    },
    bottomIconContainer: {
        flex: 0.5,
        flexDirection: 'row',
        justifyContent: 'space-around'
    },
    bottomTextContainer: {
        flex: 0.5,
        flexDirection: 'row',
        justifyContent: 'space-around'
    },
    bottomText: {
        color: 'white',
        marginTop: 5,
        fontSize: 17,
    },
    AnimeContainer: {
        flex: 1,
        padding: 8,
    }
});
