import React, { useEffect, useState } from 'react';
import {
    StyleSheet, Text, View, StatusBar, SafeAreaView, DataProvider, TouchableWithoutFeedback, Image, FlatList, Platform, ActivityIndicator, ScrollView, SectionList
} from 'react-native';
import { useDimensions} from '@react-native-community/hooks';
import { render } from 'react-dom';
import { WebView } from 'react-native-webview';
import { FontAwesomeIcon } from '@fortawesome/react-native-fontawesome'
import {
    faHistory, faHome, faSearch, faBookmark, faEllipsisV, faLanguage
} from './node_modules/@fortawesome/free-solid-svg-icons/index';
import { NavigationContainer } from '@react-navigation/native';
import { createStackNavigator } from '@react-navigation/stack';

// Click Anime Functio
