import {
    StyleSheet, LogBox, Text, View, StatusBar, SafeAreaView, Dimensions, Button, DataProvider, Picker, DrawerLayoutAndroid, TouchableWithoutFeedback, Image, FlatList, Platform, ActivityIndicator, ScrollView, SectionList, KeyboardAvoidingView, TouchableHighlight, Alert
} from 'react-native';
export const styles = StyleSheet.create({
        container: {
            flex: 1,
            backgroundColor: '#333131',
        },
        lightMode: {
            flex: 1,
            backgroundColor: 'white',
        }
        ,
        top: {
            backgroundColor: '#ed1915',
            paddingTop: Platform.OS === "android" ? StatusBar.currentHeight - 5 : 0,
            width: '100%',
            height: '10%',
            paddingLeft: 10,
            paddingRight: 10,
            flexDirection: "row",
            justifyContent: "space-between",
            alignItems: "center",
        },
        Text: {
            color: 'white',
            fontSize: 24,
            alignSelf: "flex-start",
        },
        bottom: {
            backgroundColor: '#ed1915',
            width: '100%',
            height: '8%',
            justifyContent: 'center',
            bottom: 0,
        },
        flaTContain: {
            justifyContent: 'center'
        },
        searchIcon: {
            alignSelf: 'center'
        },
        bottomIcons: {
        },
        iconContainer: {
            flexDirection: "row",
            justifyContent: 'space-around',
            flex: 0.65,
            height: 35,
            marginTop: 15
        },
        bottomIconContainer: {
            flex: 0.5,
            flexDirection: 'row',
            justifyContent: 'space-around'
        },
        bottomTextContainer: {
            flex: 0.5,
            flexDirection: 'row',
            justifyContent: 'space-around'
        },
        bottomText: {
            color: 'white',
            marginTop: 3,
            fontSize: 17,
        },
        AnimeContainer: {
            flex: 1,
            padding: 8,
            zIndex: 0
        }
    });
