import React from 'react';
import { StyleSheet, Text, View, StatusBar, SafeAreaView, TouchableNativeFeedback, Image , FlatList} from 'react-native';
import { useDimensions} from '@react-native-community/hooks';
import { render } from 'react-dom';


export default function App() {
    // Functions
    const searchPress = () => {
        alert("lol");
    }
    const getSubAnime = () => {
        return fetch('https://gogoanime.vc')
            .then((response) => response.text())
            .then(function(data){
                var n = data.split(`<ul class="items">`);
                var m = n[1].split(`</ul>`);
                var o = m[0].replace("img", "");
                console.log(o);
            })
    }
    getSubAnime()

    return (
        <SafeAreaView style={styles.container}>
            <View style={styles.top}>
                <Text style={styles.Text}>Anistream</Text>
                <View style={styles.iconContainer}>
                    <FontAwesomeIcon size={22} color={"white"} icon={faSearch}
                        style={styles.searchIcon}
                    />
                    <FontAwesomeIcon size={22} color={"white"} icon={faBookmark}
                        style={styles.searchIcon}
                    />
                    <FontAwesomeIcon size={22} color={"white"} icon={faEllipsisV}
                        style={styles.searchIcon}
                    />
                </View>
            </View>
            <View style={styles.bottom}>
                <View style={styles.bottomIconContainer}>
                    <FontAwesomeIcon size={45} color={"white"} icon={faLanguage} style={{}}
                    />
                    <FontAwesomeIcon size={40} color={"white"} icon={faHome} style={{ marginTop: 2 }}
                    />
                    <FontAwesomeIcon size={35} color={"white"} icon={faHistory} style={{ marginTop: 5 }}
                    />
                </View>
                <View style={styles.bottomTextContainer}>
                    <Text style={[styles.bottomText, { marginLeft: 15 }]}>Dub</Text>
                    <Text style={[styles.bottomText, { marginLeft: 38 }]}>Sub</Text>
                    <Text style={[styles.bottomText, { marginLeft: 20 }]}>Recent</Text>
                </View>
            </View>
        </SafeAreaView>
    );
}
const styles = StyleSheet.create({
    container: {
        flex: 1,
        backgroundColor: '#333131',
    },
    top: {
        backgroundColor: '#ed1915',
        paddingTop: Platform.OS === "android" ? StatusBar.currentHeight : 0,
        width: '100%',
        height: '10%',
        paddingLeft: 10,
        paddingRight: 10,
        flexDirection: "row",
        justifyContent: "space-between",
        alignItems: "center"
    },
    Text: {
        color: 'white',
        fontSize: 24,
        alignSelf: "flex-start",
    },
    bottom: {
        backgroundColor: '#ed1915',
        width: '100%',
        height: '8%',
        justifyContent: 'center',
        position: 'absolute',
        bottom: 0,
    },
    searchIcon: {
        marginBottom: 15,
        marginLeft: 30
    },
    bottomIcons: {

    },
    iconContainer: {
        flexDirection: "row",
    },
    bottomIconContainer: {
        flex: 0.5,
        flexDirection: 'row',
        justifyContent: 'space-around'
    },
    bottomTextContainer: {
        flex: 0.5,
        flexDirection: 'row',
        justifyContent: 'space-around'
    },
    bottomText: {
        color: 'white',
        marginTop: 5,
        fontSize: 17,
    }
});
